(params,curReqName) => {
    var paramsJSON = pm.globals.get(params);
    var globeJSON = JSON.parse(paramsJSON);
    console.log("Globe JSON - "+globeJSON);

    var editoSettings = pm.globals.get("Editor_Settings");
    var i = parseInt(pm.globals.get("editorAPIOInteg_i"));
    console.log(i);

    var editoSettingsArray = JSON.parse(editoSettings);

    console.log("Editor Settins Length - "+editoSettingsArray.length);

    var curReqCallBackSettings = "";
    if(i < editoSettingsArray.length){
        for(var key in globeJSON){
            var callBackSettings = pm.globals.get(key);
            var callBackArray = JSON.parse(callBackSettings);
            if(callBackArray.length >= i){
                curReqCallBackSettings = JSON.stringify(callBackArray[i]);
                pm.globals.set(globeJSON[key], curReqCallBackSettings);

            }
            else{
                curReqCallBackSettings = JSON.stringify(callBackArray[callBackArray.length-1]);
                pm.globals.set(globeJSON[key], curReqCallBackSettings);
            }
        }
        if(i === editoSettingsArray.length-1){
                pm.globals.set("editorAPIOInteg_i", 0);
            }
        else{
            console.log("Not exceeded!");
            pm.globals.set("editorAPIOInteg_i", i+1);
            postman.setNextRequest(curReqName);
        }
    }

}
